definition.script:
node {
   def mvnHome = "/usr/share/apache-maven/"
   stage('Preparation') { // for display purposes
      // Get some code from a GitHub repository
      git 'https://github.com/devgrid/calculator-service.git'
      // Get the Maven tool.
      // ** NOTE: This 'M3' Maven tool must be configured
      // **       in the global configuration.
      mvnHome = tool 'Maven 3.5.2'
   }
   stage('Build') {
      // Run the maven build
      sh "'${mvnHome}/bin/mvn' -e -Dmaven.test.failure.ignore clean package"
   }
   stage('Deploy') {
      // Run the maven build
      sh "nohup mvn spring-boot:run &"
      sh "sleep 60"
     // sh "git clone https://github.com/devgrid/calculator-service-test.git"
     // mvnHome = tool 'Maven 3.5.2'
     // sh "'${mvnHome}/bin/mvn' clean test"
   }
   stage('Tests') {
      // Run the maven build
      sh "rm -rf /var/lib/jenkins/workspace/pipeline-devgrid/calculator-service-test"
      sh "git clone https://github.com/devgrid/calculator-service-test.git"
      mvnHome = tool 'Maven 3.5.2'
      sh "cd calculator-service-test && '${mvnHome}/bin/mvn' clean test"
   }
   stage('Results') {
      junit '**/target/surefire-reports/TEST-*.xml'
      archive 'target/*.jar'
   }
}
